import axios from 'axios';

const fetchStatesByCountry = async (countryName) => {
  try {
    const response = await axios.get(`https://restcountries.com/v3.1/name/${countryName}`);
    console.log(response)

    // Verifique se a resposta contém dados para o país especificado
    if (response.data.length > 0) {
      const countryData = response.data[0];

      // Verifique se o país tem informações sobre estados
      if (countryData?.subdivisions) {
        const states = Object.values(countryData.subdivisions).map((state) => state.name);
        return states;
      }
    }

    return [];
  } catch (error) {
    console.error('Erro ao buscar estados:', error);
    return [];
  }
};

export default fetchStatesByCountry;
