import axios from 'axios';

const fetchStatesByCountry = async (countryCode) => {
  try {
    const response = await axios.get(`https://api.countrystatecity.in/v1/countries/${countryCode}/states`, {
      headers: {
        'X-CSCAPI-KEY': 'API_KEY',
      },
    });

    const states = response.data.map((state) => state.name);

    return states;
  } catch (error) {
    console.error('Erro ao buscar estados:', error);
    return [];
  }
};

export default fetchStatesByCountry;
