import React, { useContext, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';
import InputText from '../../Inputs/InputText/InputText';
import Checkbox from '../../Inputs/checkbox/Checkbox';


const AddTransaction = () => {
  const [isProfit, setIsProfit] = useState(false);
  const [name, setName] = useState('Transaction');

  const {
    setFinances
  } = useContext(pocketContext);

  return(
    <div className='add-finance'>
      <p>Add transaction</p>
      <div className='row s-evenly'>
        <Checkbox name="Profit" onClick={ () => { setIsProfit(true) } } />
        <Checkbox name="Expenses" onClick={ () => { setIsProfit(false) } } />
        <InputText placename="name" callback={ ({ target: { value } }) => { console.log(value) } }/>
        <input type='number'/>
      </div>
    </div>
  )
};

AddTransaction.propTypes = {};

AddTransaction.defaultProps = {};

export default AddTransaction;
