import React from 'react';
import PropTypes from 'prop-types';


const HourlyCard = ({ time, hasTask, callback, task }) => {
  const hour = time.slice(0, 2);
  console.log(task)
  const minutos = time.slice(3, 5);
  return (
    <div className="row">
      <div className="colunm s-center">
        <p className='i-b n-margin'>
          {
            hour
          }
        </p>
        <p className='i-b n-margin'>
          ..
        </p>
        <p className='i-b n-margin'>
          {
            minutos
          }
        </p>
      </div>
      <button className='time-card' onClick={ callback }>
        {
          hasTask ? <p>tarefa</p> : <p>adicionar tarefa</p>
        }
      </button>
    </div>
  )
};

HourlyCard.propTypes = {};

HourlyCard.defaultProps = {};

export default HourlyCard;
