import React, { useCallback, useContext } from 'react';
import PropTypes from 'prop-types';
import svgs from '../../../helpers/svg';
import pocketContext from '../../../context/pocketContext';


const TaskInformation = ({ task: { task }, id }) => {
  const {
    title,
    description,
  } = task;

  const {
    tasks,
    setTasks,
  } = useContext(pocketContext);

  const handleClick = () => {
    const filtered = tasks.map(({ cardArray }) => cardArray.map((x) => {
      if (x.id === id) {
        x.hasTask = false;
        x.taskId = 0;
      }
      return x;
    }));
    console.log(filtered);
  }

  return(
    <div className="row s-evenly task-inf">
      <div className='colunm task-inf'>
        <h1>{ title }</h1>
        <p>{ description }</p>
      </div>
    </div>
  )
};

TaskInformation.propTypes = {};

TaskInformation.defaultProps = {};

export default TaskInformation;
