import React from 'react';
import PropTypes from 'prop-types';
import svgs from '../../../helpers/svg';


const WeatherCard = ({ weatherData }) => {
  const arrayDias = weatherData.forecast.forecastday[0].hour.map((x) => {
    const { chance_of_rain, temp_c } = x;
    return {
      rain: chance_of_rain,
      temp_c,
      icon: x.condition.icon,
    }
  })

  console.log(arrayDias);

  return (
    <>
    <div className='weather-div column s-evenly'>
      <h1>
        {
          weatherData.location.name
        }
      </h1>
      <p className='gray'>
        {
          `${weatherData.location.region}, ${weatherData.location.country}`
        }        
      </p>
      <h1>{ weatherData.current.condition.text }</h1>
      <img src={ weatherData.current.condition.icon } alt='condition img' />
      <div className='row temp-div'>
        <h1 className='temp'>
          {
            weatherData.current.temp_c + '째C'
          }
        </h1>
        <div className='line-v'></div>
        <h1 className='temp'>
          {
            weatherData.current.temp_f + '째F'
          }
        </h1>
      </div>
    </div>
    <div className='subs row s-evenly'>
      <div className='humidity column'>
        {
          svgs.water()
        }
        <p>
          {
            weatherData.current.humidity + '%'
          }
        </p>
      </div>
      <div className='humidity column'>
        {
          svgs.wind()
        }
        <p>
          {
            weatherData.current.wind_kph + 'km/h'
          }
        </p>
      </div>
    </div>
    </>
)};

WeatherCard.propTypes = {};

WeatherCard.defaultProps = {};

export default WeatherCard;
