import React, { useContext, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';

const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"
];

const OverViewFinance = ({ month, finances }) => {
  const date = new Date();
  const [total, setTotal] = useState(0);

  useEffect(() => {
    setTotal(finances.filter((x) => x.month === month)[0].transactions.map((x) => x.value).reduce((x, sum) => x + sum, 0));
  }, [])

  return (
    <div className='overview-finance'>
      <p>
        Your balance
      </p>
      <h1>
        $ { total }
      </h1>
    </div>
  )
};

OverViewFinance.propTypes = {};

OverViewFinance.defaultProps = {};

export default OverViewFinance;
