import React, { useContext, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';
import svgs from '../../../helpers/svg';
import NewNote from '../NewNote/NewNote';


const TaskList = () => {
  const [newNote, setNewNote] = useState(false);
  const {
    notes,
    setNotes
  } = useContext(pocketContext);

  return(
    <div className='row'>
      {
        !newNote ? 
        (
          notes.length > 0 ? <div>
            {
              notes.map(({ title, description, color }) => (
                <div style={ { backgroundColor: color } }>
                  <h1>{ title }</h1>
                  <p>{ description }</p>
                </div>
              ))
            }
            <button className='note-btn' onClick={() => { setNewNote(!newNote) }}>
            {
              svgs.note()
            }
            </button>
          </div> : 
          <button className='note-only-btn' onClick={() => { setNewNote(!newNote) }}>
          {
            svgs.note()
          }
          </button>
        ) :  <NewNote setNewNote={ setNewNote } setNote={ setNotes } notes={ notes } />
      }
    </div>
  )
};

TaskList.propTypes = {};

TaskList.defaultProps = {};

export default TaskList;
