import React, { useState } from 'react';
import svgs from '../../../helpers/svg';


const TransactionCard = ({ array, color, finances, callback, isProfit, month }) => {
  const [open, setOpen] = useState(false);
  const date = new Date();
  const monthNumber = date.getMonth() > 9 ? date.getMonth() : `0${date.getMonth()}`;
  const name = Object.keys(array)[0]
  const total = array[name].map((x) => x.value).reduce((x, count) => x + count, 0);

  const handleDelete = () => {
    const index = finances.indexOf(finances.find((x) => x.month === month));
    const test = finances;
    if (isProfit) {
      test[index].profit =  finances[index].profit.filter((y) => y.type !== name)
    } else {
      console.log('s')
      test[index].expenses =  finances[index].expenses.filter((y) => y.type !== name)
    }
    console.log(test, name)
    callback(test)
  }
  return (
    <div className='type-div'>
      <div className='tran-btn row s-btw'>
        <button onClick={handleDelete}>
          {
            svgs.exit()
          }
        </button>
        <button className='row s-btw in-btn' onClick={ () => setOpen(!open) }>
          <h1>
            {
              name
            }
          </h1>
          <div className='row'>
            <h1 style={ { color: `${ color }` } }>
              {
                "$ " + total
              }
            </h1>
            {
              array[name].length > 0 ? svgs.dropdow() : null
            } 
          </div>
        </button>
      </div>
      {
        open ? (
          array[name].map(({ value, name, date }) => (
            <div className='expense-card row s-btw scale-in-left' style={ { borderBottom: `3px solid ${color}` } }>
              <div className='colunm'>
                <h1>
                  {
                    name
                  }
                </h1>
                <div className='row s-btw'>
                  <p>
                    {
                      `${monthNumber}/${date > 9 ? date : `0${date}`}`
                    }
                  </p>
                </div>
              </div>
              <p>
                $
                {
                  value
                }
              </p>
            </div>
          ))
        ) : null
      }
    </div>
  )
};

TransactionCard.propTypes = {};

TransactionCard.defaultProps = {};

export default TransactionCard;

