import axios from 'axios';

const geonamesUsername = 'humbertocastro';
const baseUrl = 'http://api.geonames.org';

const fetchStatesByCountry = async (countryName) => {
  try {
    const response = await axios.get(`${baseUrl}/searchJSON`, {
      params: {
        q: '',
        country: countryName,
        featureCode: 'ADM1',
        username: geonamesUsername,
      },
    });

    // Processar os resultados para extrair os nomes dos estados
    const states = response.data.geonames.map((state) => state.name);

    return states;
  } catch (error) {
    console.error('Erro ao buscar estados:', error);
    return [];
  }
};

export default fetchStatesByCountry;
