import React, { useContext, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import HourlyCard from '../HourlyCard/HourlyCard';
import pocketContext from '../../../context/pocketContext';
import SetNewTask from '../SetNewTask/SetNewTask';
import '../weekly.css'

const weekday = ["Sun","Mon","Tue","Wed","Thur","Fri","Sat"];


const Daily = () => {
  const {
    tasks,
    setTasks,
    openInterface,
    interfaceNewTask,
  } = useContext(pocketContext);

  const [taskId, setTaskId] = useState(0);
  const [taskWeak, setTaskWeak] = useState("");
  const [selected, setSelected] = useState("Sun");
  const [onlyTasks, setOnly] = useState(false);
  const [taskDisplay, setTaskDisplay] = useState(tasks);


  const addTask = (id, weak) => {
    setTaskId(id);
    setTaskWeak(weak);
    openInterface(true);
  }

  const handleClick = ({ target: { name } }) => {
    setSelected(name);
  }

  const handleDelete = (id) => {
    const filtered = tasks;
    filtered.forEach(({ cardArray }) => cardArray.forEach((x) => {
      if (x.id === id) {
        x.hasTask = false;
        x.taskId = 0;
      }
    }));
    filtered[0].cardArray[0].color = "black";
    setTasks(filtered);
    setTaskDisplay(filtered);
  }

  const renderTheTasks = (displayTasks) => {
    const tasksToRender = onlyTasks ? displayTasks.filter(({ weak }) => weak === selected)[0].cardArray.filter((x) => !(x.overlap) && x.hasTask).map((task) => 
    (<HourlyCard deleteCallback={ handleDelete } callback={ addTask } task={ task } />)) :
    displayTasks.filter(({ weak }) => weak === selected)[0].cardArray.filter((x) => !(x.overlap)).map((task) => 
    (<HourlyCard deleteCallback={ handleDelete } callback={ addTask } task={ task } />));
    if (tasksToRender.length > 0) {
      return tasksToRender;
    } return (<h1 className='no-task'>No tasks to be displayed</h1>)
  }

  console.log('rendereizo')

  return(
    <div className="colunm s-evenly">
      <div className='row s-evenly'>
        {
          weekday.map((day) => (
            <button
              name={ day }
              className={ selected === day ? "selected s-scale" : null }
              onClick={ handleClick }
            >
              {
                day
              }
            </button>
          ))
        }
      </div>
      <label class="cyberpunk-checkbox-label">
      <input class="cyberpunk-checkbox" type="checkbox" onClick={ ({ target }) => {
        setOnly(!onlyTasks);
      } }/>Show only tasks</label>
      {
        renderTheTasks(taskDisplay)
      }
      {
        interfaceNewTask ? <SetNewTask id={ taskId } weak={ taskWeak } /> : null
      }
    </div>
  )
};

Daily.propTypes = {};

Daily.defaultProps = {};

export default Daily;
