import React, { useContext, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';

const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"
];

const OverViewFinance = () => {
  const date = new Date();
  const [total, setTotal] = useState(0);

  const {
    finances,
  } = useContext(pocketContext);

  useEffect(() => {
    setTotal(finances.filter((x) => x.month === monthNames[date.getMonth()])[0].transactions.map((x) => x.value).reduce((x, sum) => x + sum, 0));
  }, [])

  return (
    <div className='overview-finance'>
      <h1>
        $ { total },00
      </h1>
    </div>
  )
};

OverViewFinance.propTypes = {};

OverViewFinance.defaultProps = {};

export default OverViewFinance;
