import React, { useContext, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';
import svgs from '../../../helpers/svg';
import Checkbox from '../../Inputs/checkbox/Checkbox';
import ColorsSelect from '../../Inputs/ColorsSelect/ColorsSelect';
import InputText from '../../Inputs/InputText/InputText';

const weekday = ["Sun","Mon","Tue","Wed","Thur","Fri","Sat"];
const tempos = ['00:30', '01:00', '01:30', '02:00', '02:30', '03:00', '03:30', '04:00'];

const SetNewTask = ({ id, weak }) => {  
  const [title, setTitle] = useState('Task title');
  const [description, setDescription] = useState('Task description');
  const [weakDays, setWeakDays] = useState([]);
  const [onlyOnce, setOnlyOnce] = useState(false);
  const [duration, setDuration] = useState(1);
  const [color, setColor] = useState('#00000');
  const [rmBtn, setRmBtn] = useState(false);

  const {
    openInterface,
    tasks,
    setTasks,
    setNofTasks,
    nTasks,
  } = useContext(pocketContext);

  useEffect(() => {
    console.log(tasks);
    const currentTask = tasks.find((day) => day.weak === weak)?.cardArray.find((x) => x.id === id);
    setWeakDays([weak]);
    setTitle(currentTask?.task.title || 'Task title');
    setDescription(currentTask?.task.description || 'Task description');
    if (currentTask?.taskId !== 0) {
      const repeatingWeeks = tasks
        .filter(({ cardArray }) => cardArray.some((x) => x.taskId === currentTask.taskId))
        .map(({ weak }) => weak);
      setWeakDays(repeatingWeeks);
      setRmBtn(true);
    }
    const newTaskObject = document.querySelector(".new-task");
    newTaskObject.style.top = document.documentElement.scrollTop + 'px';
  }, [onlyOnce, id, tasks, weak]);

  const handleChange = (event) => {
    const { value, name } = event.target;
    name === "title" ? setTitle(value) : setDescription(value);
  };

  const handleWeakClick = (event) => {
    const { name } = event.target;
    weakDays.includes(name) ? setWeakDays(weakDays.filter((day) => day !== name)) : setWeakDays([...weakDays, name]);
  };

  const handleDelete = () => {
    const filter = tasks.map((task) => ({
      ...task,
      cardArray: task.cardArray.map((item) => {
        if (item.taskId === id) {
          return {
            ...item,
            hasTask: false,
            taskId: 0,
            color: 'teste',
          };
        }
        return item;
      }),
    }));
    setTasks(filter);
    setNofTasks(nTasks - 1);
    openInterface(false);
    localStorage.setItem('tasklist', JSON.stringify(filter));
  };

  const handleClick = () => {
    const newTasks = tasks.map((task) => {
      const newCardArray = task.cardArray.map((item) => {
        if (item.id > id && item.id <= id + duration) {
          return {
            ...item,
            overlap: true,
          };
        }
        return item;
      });

      const newObject = {
        title,
        description,
        duration,
      };

      const updatedItem = {
        ...newCardArray.find((x) => x.id === id),
        task: newObject,
        hasTask: true,
        taskId: nTasks + 1,
        color,
      };

      return {
        ...task,
        cardArray: [
          ...newCardArray.slice(0, id),
          updatedItem,
          ...newCardArray.slice(id + 1),
        ],
      };
    });

    setTasks(newTasks);
    setDuration(0);
    setNofTasks(nTasks + 1);
    openInterface(false);
    localStorage.setItem('tasklist', JSON.stringify(newTasks));
  };

  return (
    <div className="new-task colunm s-evenly">
      <button className='x-btn' onClick={() => openInterface(false)}>
        {svgs.exit()}
      </button>
      <InputText name="title" callback={handleChange} placename={title} />
      <InputText name="description" callback={handleChange} placename={description} />
      {!onlyOnce && (
        <div>
          Repet on:
          {weekday.map((x) => (
            <button
              key={x}
              name={x}
              className={`weak-day ${weakDays.includes(x) ? 'selected weak-day s-scale' : ''}`}
              onClick={handleWeakClick}
            >
              {x}
            </button>
          ))}
        </div>
      )}
      <Checkbox onClick={() => setOnlyOnce(!onlyOnce)} name="Show only tasks" />
      <select onChange={(event) => setDuration(event.target.value)}>
        <option value="1">How long will it last</option>
        {tempos.map((x, index) => (
          <option value={index + 1} key={`${index}-${x}`}>
            {x}
          </option>
        ))}
      </select>
      <label className='colunm s-evenly'>
        Choose the color for the task
        <ColorsSelect selectedColor={color} callback={setColor} />
      </label>
      <div className='row s-evenly'>
        <button onClick={handleClick}>
          <p>{rmBtn ? 'Update existing task' : 'Create new Task'}</p>
        </button>
        {rmBtn && (
          <button onClick={handleDelete} style={{ color: 'red' }}>
            Remove Existing task
          </button>
        )}
      </div>
    </div>
  );
};

SetNewTask.propTypes = {
  id: PropTypes.object.isRequired,
  weak: PropTypes.string.isRequired,
};

export default SetNewTask;
