import axios from 'axios';
import { parseString } from 'xml2js';

const geonamesUsername = 'SEU_NOME_DE_USUÁRIO_GEONAMES';
const baseUrl = 'http://api.geonames.org';

const fetchStatesByCountry = async (countryName) => {
  try {
    const response = await axios.get(`${baseUrl}/search`, {
      params: {
        q: '',
        country: countryName,
        adminCode1: '',
        style: 'LONG',
        maxRows: 1000,
        username: geonamesUsername,
      },
    });

    // Fazer o parsing do XML para objeto JavaScript
    let parsedData;
    parseString(response.data, (err, result) => {
      if (err) {
        throw new Error('Erro ao fazer parsing do XML:', err);
      }
      parsedData = result;
    });

    // Extrair os nomes dos estados do objeto JavaScript
    const states = parsedData.geonames.geoname.map((state) => state.adminName1);

    return states;
  } catch (error) {
    console.error('Erro ao buscar estados:', error);
    return [];
  }
};

export default fetchStatesByCountry;
