import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import WeatherForm from '../WeatherForm/WeatherForm';
import WeatherCard from '../WeatherCard/WeatherCard';
import { mockFutureWeather, weatherFake } from '../../../helpers/mockWeather';
import '../weather.css'
import pocketContext from '../../../context/pocketContext';

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);

  const {
    theme,
  } = useContext(pocketContext);

  const fake = weatherFake;

  const fetchData = async (location) => {
    const apiKey = process.env.REACT_APP_WEATHER_API_KEY;
    try {
      const response = await axios.get(`http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${location}&days=1&aqi=yes&alerts=yes`);
      console.log(response);
      setWeatherData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleClick = async (city) => {
    console.log(city);
    // const res = await fetchData(city);
    // console.log(res);    
  }

  return (
    <div>
      <WeatherForm callback={ handleClick } />
      {
        fake ? <WeatherCard weatherData={ mockFutureWeather } /> : null
      }
    </div>
  );
};

export default Weather;
