import React, { useEffect, useState } from 'react';
import axios from 'axios';
import WeatherForm from '../WeatherForm/WeatherForm';
import WeatherCard from '../WeatherCard/WeatherCard';
import { weatherFake } from '../../../helpers/mockWeather';
import '../weather.css'

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);

  const fake = weatherFake;

  const fetchData = async (location) => {
    const apiKey = process.env.REACT_APP_WEATHER_API_KEY;
    try {
      const response = await axios.get(`http://api.weatherapi.com/v1/forecast.json?key=b4944ce3f6ee45bb9e8203310230407&q=${location}&days=1&aqi=yes&alerts=yes`);
      setWeatherData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleClick = async (city) => {
    console.log(city);
    const res = fetchData(city);
    console.log(res);    
  }

  return (
    <div>
      <WeatherForm callback={ handleClick } />
      {
        fake ? <WeatherCard weatherData={ fake } /> : null
      }
    </div>
  );
};

export default Weather;
