import React, { useContext, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';
import InputText from '../../Inputs/InputText/InputText';
import Checkbox from '../../Inputs/checkbox/Checkbox';

const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"
];

const days = [];

const AddTransaction = () => {
  const [isProfit, setIsProfit] = useState(false);
  const [name, setName] = useState('Transaction');
  const [month, setMonth] = useState('May');
  days.length = 31;

  const {
    setFinances
  } = useContext(pocketContext);

  const handleSelect = ({ target: { value } }) => {

  }

  return(
    <div className='add-finance'>
      <p>Add transaction</p>
      <div className='row s-evenly'>
        <Checkbox name="Profit" onClick={ () => { setIsProfit(true) } } />
        <Checkbox name="Expenses" onClick={ () => { setIsProfit(false) } } />
        <InputText placename="name" callback={ ({ target: { value } }) => setName(value) }/>
        <input type='number' className='n-input' placeholder='Total value'/>
        <label>
          Month
          <select className='finance-select colunm' onChange={ handleSelect } value={ month }>
          {
            monthNames.map((x) => (
              <option value={x}>{ x }</option>
            ))
          }
          </select>
        </label>
        <label>
          Day
          <select className='finance-select colunm' onChange={ handleSelect } value={ month }>
          {
            monthNames.map((x) => (
              <option value={x}>{ x }</option>
            ))
          }
          </select>
        </label>        
      </div>
    </div>
  )
};

AddTransaction.propTypes = {};

AddTransaction.defaultProps = {};

export default AddTransaction;
