import React, { useContext, useState } from 'react';
import PropTypes from 'prop-types';
import pocketContext from '../../../context/pocketContext';
import svgs from '../../../helpers/svg';
import NewNote from '../NewNote/NewNote';


const TaskList = () => {
  const [newNote, setNewNote] = useState(false);
  const {
    notes,
    setNotes
  } = useContext(pocketContext);

  const renderTasks = () => (
    <div className='colunm' style={ { width: '100vw' }}>
        <button className='note-btn row s-evenly' onClick={() => { setNewNote(!newNote) }}>
        add note
          {
            svgs.note()
          }
        </button>
        <div className='row'>
          {
            notes.map(({ title, description, color }) => (
              <div style={ { backgroundColor: color } } className="note">
                <h1>{ title }</h1>
                <p>{ description }</p>
              </div>
            ))
          }
         </div> 
      </div>
  )

  return(
    <div className='row'>
      {
        !newNote ? renderTasks() : <NewNote setNewNote={ setNewNote } setNote={ setNotes } notes={ notes } />
      }
    </div>
  )
};

TaskList.propTypes = {};

TaskList.defaultProps = {};

export default TaskList;
